import React, { useEffect, useState } from 'react';
import axios from 'axios';
import L from 'leaflet';
import { io } from 'socket.io-client';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);
  const [socket] = useState(() => io('http://localhost:3000'));

  // 🔄 Récupère l'utilisateur et la commande
  useEffect(() => {
    axios.get('http://localhost:3000/api/livreur/accueil', {
      withCredentials: true
    })
    .then(res => {
        console.log('Réponse API:', res.data);
      setUser(res.data.user);
      setCommande(res.data.commande);
    })
    .catch(err => {
      console.error('Erreur dashboard :', err.response?.data || err.message);
    });
  }, []);

  // 🗺️ Initialise la carte quand une commande active est disponible
  useEffect(() => {
    if (!commande || !commande.latitude || !commande.longitude) return;

    const map = L.map('map').setView([commande.latitude, commande.longitude], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap'
    }).addTo(map);

    // Ajout des marqueurs
    L.marker([commande.latitude, commande.longitude]).addTo(map).bindPopup("📍 Client");
    L.marker([commande.latitude_li, commande.longitude_li], {
      icon: L.icon({
        iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
        iconSize: [32, 32]
      })
    }).addTo(map).bindPopup("🚚 Vous");

    // Ligne entre client et livreur
    const line = L.polyline(
      [[commande.latitude, commande.longitude], [commande.latitude_li, commande.longitude_li]],
      { color: 'blue' }
    ).addTo(map);

    map.fitBounds(line.getBounds());

    return () => map.remove(); // Nettoyage
  }, [commande]);

  // ✅ Accepter commande
  const handleAccepter = async () => {
    try {
      await axios.post(`http://localhost:3000/commande/confirmer/${commande.id}`, {
        latitude_li: commande.latitude_li,
        longitude_li: commande.longitude_li
      }, {
        withCredentials: true
      });

      // Recharge les données après acceptation
      const res = await axios.get('http://localhost:3000/api/livreur/accueil', { withCredentials: true });
      setCommande(res.data.commande);

    } catch (err) {
      console.error('Erreur acceptation :', err.response?.data || err.message);
    }
  };

  // ❌ Annuler commande
  const handleAnnuler = async () => {
    try {
      await axios.post(`http://localhost:3000/commande/annuler_li/${commande.id}`, {
        latitude_li: commande.latitude_li,
        longitude_li: commande.longitude_li
      }, {
        withCredentials: true
      });

      // Recharge
      const res = await axios.get('http://localhost:3000/api/livreur/accueil', { withCredentials: true });
      setCommande(res.data.commande);

    } catch (err) {
      console.error('Erreur annulation :', err.response?.data || err.message);
    }
  };

  if (!user) return <p>Chargement du livreur...</p>;

  return (
    <div className="dashboard-livreur">
      <h1>Bonjour {user.nom}</h1>

      {!commande || (commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 1) ? (
        <div className="alert alert-info">✅ Commande terminée ou annulée. En attente d'une autre.</div>
      ) : commande.statut === 1 && commande.statut_2 === 0 && commande.statut_3 === 0 ? (
        <>
          <table className="table table-striped">
            <thead>
              <tr><th>Client</th><th>Numéro</th><th>Date</th><th>Action</th></tr>
            </thead>
            <tbody>
              <tr>
                <td>{commande.client_nom}</td>
                <td>{commande.client_num}</td>
                <td>{new Date(commande.date_commande).toLocaleString()}</td>
                <td>
                  <button onClick={handleAccepter} className="btn btn-success btn-sm">
                    ✅ Accepter
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
          <div className="loader-container">
            <div className="loader"></div>
            <p>En attente de validation...</p>
          </div>
        </>
      ) : commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0 ? (
        <>
          <table className="table table-striped">
            <thead>
              <tr><th>Client</th><th>Numéro</th><th>Date</th><th>Action</th></tr>
            </thead>
            <tbody>
              <tr>
                <td>{commande.client_nom}</td>
                <td>{commande.client_num}</td>
                <td>{new Date(commande.date_commande).toLocaleString()}</td>
                <td>
                  <button onClick={handleAnnuler} className="btn btn-danger btn-sm">
                    ❌ Annuler
                  </button>
                </td>
              </tr>
            </tbody>
          </table>

          <div className="map-container mt-4">
            <div id="map" style={{ height: '400px' }}></div>
          </div>
        </>
      ) : null}
    </div>
  );
};

export default Dashboard;
