// === üìÅ src/pages/Dashboard.js ===
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../Lheader/Header';
import L from 'leaflet';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/barre.css';

const socket = io('http://localhost:3000');

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);
  const mapRef = React.useRef(null);
  const intervalRef = React.useRef(null);

  useEffect(() => {
    axios.get('http://localhost:3000/api/livreur/livreurAccueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommande(res.data.commande);
      })
      .catch(console.error);
  }, []);

  useEffect(() => {
    if (!commande || commande.statut !== 1 || commande.statut_2 !== 1 || commande.statut_3 !== 0) return;

    const initMap = () => {
      if (!commande.latitude || !commande.longitude) return;

      if (mapRef.current) {
        mapRef.current.remove();
      }

      const map = L.map('map').setView([commande.latitude, commande.longitude], 13);
      mapRef.current = map;

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '¬© OpenStreetMap'
      }).addTo(map);

      const markerClient = L.marker([commande.latitude, commande.longitude]).addTo(map).bindPopup("üìç Client");
      const markerLivreur = L.marker([commande.latitude_li, commande.longitude_li], {
        icon: L.icon({
          iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
          iconSize: [32, 32]
        })
      }).addTo(map).bindPopup("üöö Vous");

      const line = L.polyline(
        [[commande.latitude, commande.longitude], [commande.latitude_li, commande.longitude_li]],
        { color: 'blue' }
      ).addTo(map);

      map.fitBounds(line.getBounds());
    };

    initMap();

    intervalRef.current = setInterval(() => {
      axios.get('http://localhost:3000/api/livreur/livreurAccueil', { withCredentials: true })
        .then(res => {
          setCommande(res.data.commande);
        }).catch(console.error);
    }, 5000);

    return () => {
      clearInterval(intervalRef.current);
      if (mapRef.current) mapRef.current.remove();
    };
  }, [commande?.id]);

  useEffect(() => {
    socket.on("commandeCreated", () => window.location.reload());
    socket.on("commandeConfirmed", () => window.location.reload());
    socket.on("commandeAnnulee", () => window.location.reload());

    return () => {
      socket.off("commandeCreated");
      socket.off("commandeConfirmed");
      socket.off("commandeAnnulee");
    };
  }, []);

  if (!user) return <p>Chargement du livreur...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>
      <div className="main-content" id="continer">
        <div id="error-gps" style={{ color: 'red' }}>
          Veuillez activer votre GPS
        </div>

        <div className="dashboard-livreur">
          <h1>Bonjour {user.nom}</h1>

          {!commande || (commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 1) ? (
            <div className="alert alert-info">Commande annul√©e ou termin√©e. En attente d'une autre.</div>
          ) : commande.statut === 1 && commande.statut_2 === 0 && commande.statut_3 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Accepter</th><th>Refuser</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td>
                      <button
                        className="btn btn-success btn-sm"
                        onClick={() => {
                          axios.post(`http://localhost:3000/api/commande/confirmer/${commande.id}`, {
                            latitude_li: commande.latitude_li,
                            longitude_li: commande.longitude_li
                          }, { withCredentials: true })
                            .then(() => window.location.reload())
                            .catch(console.error);
                        }}
                      >
                        Accepter
                      </button>
                    </td>
                    <td>
                      <button
                        type="button"
                        className="btn btn-danger btn-sm"
                        onClick={() => {
                          if (confirm("Annuler la commande ?")) {
                            axios.post(`http://localhost:3000/api/commande/annuler_li/${commande.id}`, {
                              latitude_li: commande.latitude_li,
                              longitude_li: commande.longitude_li
                            }, { withCredentials: true })
                            .then(() => window.location.reload())
                            .catch(err => alert("Erreur d'annulation"));
                          }
                        }}
                      >
                        Annuler
                      </button>

                    </td>
                  </tr>
                </tbody>
              </table>
              <div className="loader-container">
                <div className="loader"></div>
                <p>En attente de validation...</p>
              </div>
            </>
          ) : commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Refuser</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td>
                      <form method="POST" action={`http://localhost:3000/commande/annuler_li/${commande.id}`}>
                        <input type="hidden" name="latitude_li" value={commande.latitude_li} />
                        <input type="hidden" name="longitude_li" value={commande.longitude_li} />
                        <button type="submit" className="btn btn-danger btn-sm">Annuler</button>
                      </form>
                    </td>
                  </tr>
                </tbody>
              </table>
              <div className="map-container mt-4">
                <div id="map" style={{ height: '400px' }}></div>
              </div>
            </>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
