import React, { useEffect, useState, useMemo, useRef } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import L from 'leaflet';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/attente.css';
import '../styles/accueil.css';

const Accueil = () => {
  const [user, setUser] = useState(null);
  const [commandes, setCommandes] = useState([]);
  const [livreurs, setLivreurs] = useState([]);
  const [commandeValidee, setCommandeValidee] = useState(null);
  const [distanceRestante, setDistanceRestante] = useState(null);
  const [tempsEcoule, setTempsEcoule] = useState(0);
  const [commandeDebut, setCommandeDebut] = useState(null);
  const commandesData = useRef({});
  const mapRef = useRef(null);
  const intervalRef = useRef(null);
  const gpsInterval = useRef(null);

  // Vérification GPS continue
  useEffect(() => {
    gpsInterval.current = setInterval(() => {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          commandesData.current.latitude = pos.coords.latitude;
          commandesData.current.longitude = pos.coords.longitude;
        },
        () => {
          alert("⛔ Veuillez activer votre GPS pour continuer.");
          clearInterval(gpsInterval.current);
        }
      );
    }, 5000);

    return () => clearInterval(gpsInterval.current);
  }, []);

  useEffect(() => {
    axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommandes(res.data.commandes);
        setLivreurs(res.data.livreurs);

        const cmd = res.data.commandes[0];
        if (cmd && cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0) {
          setCommandeValidee(cmd);
          setCommandeDebut(Date.now());
        }
      })
      .catch(console.error);
  }, []);

  useEffect(() => {
    const socket = io('http://localhost:3001');
    console.log("🟢 Connecté à Socket.io");

    socket.on('livreurStatusChange', ({ id, status }) => {
      if (status === 'online') {
        axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
          .then(res => setLivreurs(res.data.livreurs))
          .catch(console.error);
      } else if (status === 'offline') {
        setLivreurs(prev => prev.filter(l => l.id !== id));
      }
    });

    socket.on("commandeCreated", () => location.reload());
    socket.on("commandeConfirmed", () => location.reload());
    socket.on("update", () => location.reload());
    socket.on("commandeAnnulee", (data) => {
      alert(data.message);
      location.reload();
    });

    return () => socket.disconnect();
  }, []);

  useEffect(() => {
    if (commandeValidee && commandesData.current.latitude && commandesData.current.longitude) {
      if (!mapRef.current) {
        mapRef.current = L.map('map').setView([
          commandesData.current.latitude,
          commandesData.current.longitude
        ], 13);

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '&copy; OpenStreetMap contributors'
        }).addTo(mapRef.current);
      }

      const clientLatLng = L.latLng(commandesData.current.latitude, commandesData.current.longitude);
      const livreurLatLng = L.latLng(commandeValidee.latitude_li, commandeValidee.longitude_li);
      const route = L.polyline([livreurLatLng, clientLatLng], { color: 'blue' }).addTo(mapRef.current);
      mapRef.current.fitBounds(route.getBounds());

      if (intervalRef.current) clearInterval(intervalRef.current);
      intervalRef.current = setInterval(() => {
        const now = Date.now();
        const seconds = Math.floor((now - commandeDebut) / 1000);
        setTempsEcoule(seconds);

        const distance = clientLatLng.distanceTo(livreurLatLng) / 1000;
        setDistanceRestante(distance.toFixed(2));
      }, 1000);
    }

    return () => clearInterval(intervalRef.current);
  }, [commandeValidee]);

  const formVisible = useMemo(() => {
    return commandes.length === 0 || commandes.every(cmd => cmd.statut_3 === 1);
  }, [commandes]);

  const handleGeoSubmit = (livreur) => {
    navigator.geolocation.getCurrentPosition(
      pos => {
        commandesData.current = {
          client_id: user.id,
          client_nom: user.nom,
          client_num: user.telephone,
          livreur_id: livreur.id,
          livreur_nom: livreur.nom,
          livreur_marque_moto: livreur.marque_moto,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
          statut: "1",
          statut_2: "0",
          statut_3: "0",
          disponibilite: "0"
        };

        const tempForm = document.createElement("form");
        tempForm.method = "POST";
        tempForm.action = "/commande/html/page/accueil";

        for (let key in commandesData.current) {
          const input = document.createElement("input");
          input.type = "hidden";
          input.name = key;
          input.value = commandesData.current[key];
          tempForm.appendChild(input);
        }

        document.body.appendChild(tempForm);
        tempForm.submit();
      },
      err => {
        alert("⛔ GPS non autorisé. Impossible de continuer.");
      }
    );
  };

  if (!user) return <p>Chargeoooment...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>

      <div className="main-content client" id="continer">
        {formVisible && (
          <>
            <h1>Bienvenue {user.nom}</h1>
            <div className="all-form" id="okjik">
              <div id="zone-livreurs">
                <h3>Les livreurs</h3>
                {livreurs.length === 0 ? (
                  <div className="alert alert-warning text-center">
                    🛑 Aucun livreur n’est actuellement en ligne.
                  </div>
                ) : (
                  livreurs.map(livreur => (
                    <div
                      key={livreur.id}
                      id={`livreur-${livreur.id}`}
                      className={`livreur-card ${livreur.est_occupe ? 'occupe' : 'disponible'}`}
                    >
                      <img src="/assets/img/service-livraison-concept-masque.png" alt="livreur" />
                      <span className="livreur-nom">{livreur.nom}</span>
                      <span>{livreur.marque_moto}</span>
                      <div className="commander">
                        {livreur.est_occupe ? (
                          <span className="statut" style={{ color: 'red' }}>
                            🔴 Occupé
                          </span>
                        ) : (
                          <>
                            <input
                              type="button"
                              value="Commander"
                              onClick={() => handleGeoSubmit(livreur)}
                            />
                            <span className="statut" style={{ color: 'green', display: 'block' }}>
                              🟢 Disponible
                            </span>
                          </>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </>
        )}

        {commandes.map(cmd => {
          const date = new Date(cmd.date_commande).toLocaleString();

          if (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) {
            return (
              <div key={cmd.id} className="en-attente">
                <table className="table table-striped mt-4">
                  <thead>
                    <tr><th>Livreur</th><th>Moto</th><th>Statut</th><th>Date</th><th>Action</th></tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{cmd.livreur_nom}</td>
                      <td>{cmd.livreur_marque_moto}</td>
                      <td>En attente</td>
                      <td>{date}</td>
                      <td>
                        <form
                          action={`/commande/annuler/${cmd.id}`}
                          method="POST"
                          onSubmit={() => confirm('Annuler ?')}
                        >
                          <button className="btn btn-danger btn-sm">Annuler</button>
                        </form>
                      </td>
                    </tr>
                  </tbody>
                </table>
                <div className="loader-container">
                  <div className="loader"></div>
                  <p>En attente de validation par le livreur...</p>
                </div>
              </div>
            );
          }

          return null;
        })}

        {commandeValidee && (
          <div className="suivi-commande">
            <div id="map" style={{ height: '400px', width: '100%' }}></div>
            <div className="infos-commande">
              <p>📏 Distance restante : {distanceRestante} km</p>
              <p>⏱ Temps écoulé : {tempsEcoule} sec</p>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default Accueil;
