import React, { useEffect, useState, useMemo, useRef } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/attente.css';
import '../styles/accueil.css';

const Accueil = () => {
  const [user, setUser] = useState(null);
  const [commandes, setCommandes] = useState([]);
  const [livreurs, setLivreurs] = useState([]);
  const commandesData = useRef({});

  const fetchData = () => {
    axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommandes(res.data.commandes);
        setLivreurs(res.data.livreurs);
      })
      .catch(console.error);
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const socket = io('http://localhost:3000');
    console.log("ðŸŸ¢ ConnectÃ© Ã  Socket.io");

    socket.on('livreurStatusChange', () => {
      fetchData();
    });

    socket.on("commandeCreated", () => fetchData());
    socket.on("commandeConfirmed", () => fetchData());
    socket.on("update", () => fetchData());
    socket.on("commandeAnnulee", (data) => {
      alert(data.message || 'Une commande a Ã©tÃ© annulÃ©e.');
      fetchData();
    });

    return () => socket.disconnect();
  }, []);

  const formVisible = useMemo(() => {
    return !commandes.some(cmd =>
      (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) ||
      (cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0)
    );
  }, [commandes]);

  const handleGeoSubmit = (livreur) => {
    navigator.geolocation.getCurrentPosition(
      pos => {
        commandesData.current = {
          client_id: user.id,
          client_nom: user.nom,
          client_num: user.telephone,
          livreur_id: livreur.id,
          livreur_nom: livreur.nom,
          livreur_marque_moto: livreur.marque_moto,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
          statut: "1",
          statut_2: "0",
          statut_3: "0",
          disponibilite: "0"
        };

        const tempForm = document.createElement("form");
        tempForm.method = "POST";
        tempForm.action = "/commande/html/page/accueil";

        for (let key in commandesData.current) {
          const input = document.createElement("input");
          input.type = "hidden";
          input.name = key;
          input.value = commandesData.current[key];
          tempForm.appendChild(input);
        }

        document.body.appendChild(tempForm);
        tempForm.submit();
      },
      () => {
        alert("â›” GPS non autorisÃ©. Impossible de continuer.");
      }
    );
  };

  if (!user) return <p>Chargement...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>

      <div className="main-content" id="continer">
        {formVisible && (
          <>
            <h1>Bienvenue {user.nom}</h1>
            <div className="all-form" id="okjik">
              <div id="zone-livreurs">
                <h3>Les livreurs</h3>
                {livreurs.length === 0 ? (
                  <div className="alert alert-warning text-center">
                    ðŸ›‘ Aucun livreur nâ€™est actuellement en ligne.
                  </div>
                ) : (
                  livreurs.map(livreur => (
                    <div
                      key={livreur.id}
                      id={`livreur-${livreur.id}`}
                      className={`livreur-card ${livreur.est_occupe ? 'occupe' : 'disponible'}`}
                    >
                      <img src="/assets/img/service-livraison-concept-masque.png" alt="livreur" />
                      <span className="livreur-nom">{livreur.nom}</span>
                      <span>{livreur.marque_moto}</span>
                      <div className="commander">
                        {livreur.est_occupe ? (
                          <span className="statut" style={{ color: 'red' }}>
                            ðŸ”´ OccupÃ©
                          </span>
                        ) : (
                          <>
                            <input
                              type="button"
                              value="Commander"
                              onClick={() => handleGeoSubmit(livreur)}
                            />
                            <span className="statut" style={{ color: 'green', display: 'block' }}>
                              ðŸŸ¢ Disponible
                            </span>
                          </>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </>
        )}

        {commandes.map(cmd => {
          const date = new Date(cmd.date_commande).toLocaleString();

          if (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) {
            return (
              <div key={cmd.id}>
                <table className="table table-striped mt-4">
                  <thead>
                    <tr><th>Livreur</th><th>Moto</th><th>Statut</th><th>Date</th><th>Action</th></tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{cmd.livreur_nom}</td>
                      <td>{cmd.livreur_marque_moto}</td>
                      <td>En cours</td>
                      <td>{date}</td>
                      <td>
                        <form
                          action={`/commande/annuler/${cmd.id}`}
                          method="POST"
                          onSubmit={() => confirm('Annuler ?')}
                        >
                          <button className="btn btn-danger btn-sm">Annuler</button>
                        </form>
                      </td>
                    </tr>
                  </tbody>
                </table>
                <div className="loader-container">
                  <div className="loader"></div>
                  <p>En attente de validation par le livreur...</p>
                </div>
              </div>
            );
          }

          return null;
        })}
      </div>
    </>
  );
};

export default Accueil;
