import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import Header from '../Lheader/Header';
import L from 'leaflet';
import { useNavigate } from 'react-router-dom';
import 'leaflet-routing-machine';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/acc-livreur.css';

const socket = io('http://localhost:3000');

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);
  const [position, setPosition] = useState(null);
  const [gpsError, setGpsError] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [distanceRestante, setDistanceRestante] = useState(null);
  const [mapKey, setMapKey] = useState(0); // ➤ pour forcer le rerender du container

  const mapRef = useRef(null);
  const routingControlRef = useRef(null);

  // Charger infos utilisateur et commande
  useEffect(() => {
    axios.get('http://localhost:3000/api/commande/livreurAccueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommande(res.data.commande);
      })
      .catch((err) => {
  console.error('Erreur Axios :', err.response?.status, err.response?.data || err.message);
});

  }, []);

  // Position GPS mise à jour toutes les 5s
  useEffect(() => {
    const updateGPS = () => {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setGpsError(false);
          const coords = {
            latitude: pos.coords.latitude,
            longitude: pos.coords.longitude
          };
          setPosition(coords);

          axios.post('http://localhost:3000/api/commande/updatePosition', {
            latitude_li: coords.latitude,
            longitude_li: coords.longitude
          }, { withCredentials: true });
        },
        (err) => {
          console.warn('GPS error:', err.message);
          setGpsError(true);
        }
      );
    };

    updateGPS();
    const interval = setInterval(updateGPS, 5000);
    return () => clearInterval(interval);
  }, []);

  // Affichage carte + itinéraire
  useEffect(() => {
    if (!commande || commande.statut !== 1 || commande.statut_2 !== 1 || commande.statut_3 !== 0 || !position) return;

    // Forcer le remount du container
    setMapKey(prev => prev + 1);

  }, [commande, position]);

  useEffect(() => {
    if (!commande || commande.statut !== 1 || commande.statut_2 !== 1 || commande.statut_3 !== 0 || !position) return;

    const mapElement = document.getElementById('map');
    if (mapElement) mapElement.innerHTML = ''; // 🧼 Vider le conteneur avant de créer une nouvelle carte

    const map = L.map('map').setView([position.latitude, position.longitude], 13);
    mapRef.current = map;

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; OpenStreetMap contributors'
    }).addTo(map);

    const start = L.latLng(position.latitude, position.longitude);
    const end = L.latLng(commande.latitude, commande.longitude);

    const routingControl = L.Routing.control({
      waypoints: [start, end],
      routeWhileDragging: false,
      lineOptions: { styles: [{ color: 'blue', weight: 5 }] },
      addWaypoints: false,
      createMarker: () => null
    }).addTo(map);

    routingControlRef.current = routingControl;

    routingControl.on('routesfound', (e) => {
      const route = e.routes[0];
      setDistanceRestante((route.summary.totalDistance / 1000).toFixed(2));
    });

    if (!startTime) setStartTime(Date.now());

    return () => {
      if (routingControlRef.current) routingControlRef.current.remove();
      if (mapRef.current) mapRef.current.remove();
    };
  }, [mapKey]);

  // Sockets
  useEffect(() => {
    socket.on("commandeCreated", () => window.location.reload());
    socket.on("commandeConfirmed", () => window.location.reload());
    socket.on("commandeAnnulee", () => window.location.reload());

    return () => {
      socket.off("commandeCreated");
      socket.off("commandeConfirmed");
      socket.off("commandeAnnulee");
    };
  }, []);

  const getElapsedTime = () => {
    if (!startTime) return '00:00';
    const diff = Date.now() - startTime;
    const mins = Math.floor(diff / 60000);
    const secs = Math.floor((diff % 60000) / 1000);
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

const navigate = useNavigate();

useEffect(() => {
  if (!user) {
    setTimeout(() => navigate('/'), 100); // redirection vers la page d’accueil
  }
}, [user]);

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>

      <div className="main-content" id="continer">
        {gpsError && (
          <div style={{ color: 'red', fontWeight: 'bold' }}>🚨 Veuillez activer votre GPS</div>
        )}

        <div className="dashboard-livreur">
          <h2>Bonjour {user.nom}</h2>

          {!commande || commande.statut_3 === 1 ? (
            <div className="alert alert-secondary">Aucune demande livraison en cours</div>
          ) : commande.statut === 1 && commande.statut_2 === 0 && commande.statut_3 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Numéro</th><th>Date</th><th>Accepter</th><th>Annuler</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td>
                      <button
                        className="btn btn-success btn-sm"
                        onClick={() => {
                          axios.post(`http://localhost:3000/api/commande/confirmer/${commande.id}`, {
                            latitude_li: position?.latitude,
                            longitude_li: position?.longitude
                          }, { withCredentials: true })
                            .then(() => window.location.reload())
                            .catch(console.error);
                        }}
                      >
                        Accepter
                      </button>
                    </td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => {
                          if (confirm('Annuler la commande ?')) {
                            axios.post(`http://localhost:3000/api/commande/annuler_li/${commande.id}`, {
                              latitude_li: position?.latitude,
                              longitude_li: position?.longitude
                            }, { withCredentials: true })
                              .then(() => window.location.reload())
                              .catch(err => alert("Erreur d'annulation"));
                          }
                        }}
                      >
                        Annuler
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
              <div className="loader-container">
                <div className="loader"></div>
                <p>📦 En attente de validation...</p>
              </div>
            </>
          ) : commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0 ? (
            <>
              <h4>Livraison en cours</h4>
              <div id="map" key={mapKey} style={{ height: '400px', width: '100%' }}></div>
              <p className="mt-3">
                📏 Distance restante : <strong>{distanceRestante ? `${distanceRestante} km` : 'Calcul...'}</strong><br />
                ⏱ Temps écoulé : <strong>{getElapsedTime()}</strong>
              </p>
              <form method="POST" action={`http://localhost:3000/commande/annuler_li/${commande.id}`}>
                <input type="hidden" name="latitude_li" value={position?.latitude || ''} />
                <input type="hidden" name="longitude_li" value={position?.longitude || ''} />
                <button className="btn btn-danger btn-sm mt-2">Annuler</button>
              </form>
            </>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
