//=============== Accueil.jsx ===============

import React, { useEffect, useState, useMemo, useRef } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import L from 'leaflet';
import 'leaflet-routing-machine';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/attente.css';
import '../styles/accueil.css';


const Accueil = () => {
  const [user, setUser] = useState(null);
  const [commandes, setCommandes] = useState([]);
  const [livreurs, setLivreurs] = useState([]);
  const [distance, setDistance] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const commandesData = useRef({});
  const mapRef = useRef(null);
  const routingRef = useRef(null);
  const socketRef = useRef(null);


  // IcÃ´nes personnalisÃ©es
  const livreurIcon = L.icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
    iconSize: [40, 40],
    iconAnchor: [20, 40],
    popupAnchor: [0, -40]
  });

  const clientIcon = L.icon({
    iconUrl: 'https://cdn-icons-png.flaticon.com/512/1077/1077012.png',
    iconSize: [40, 40],
    iconAnchor: [20, 40],
    popupAnchor: [0, -40]
  });


  useEffect(() => {
    if (!document.getElementById('leaflet-css')) {
      const link = document.createElement('link');
      link.id = 'leaflet-css';
      link.rel = 'stylesheet';
      link.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css';
      document.head.appendChild(link);
    }
  }, []);

  useEffect(() => {
    axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommandes(res.data.commandes || []);
        setLivreurs(res.data.livreurs || []);
      })
      .catch(console.error);
  }, []);

  useEffect(() => {
    const gpsInterval = setInterval(() => {
      navigator.geolocation.getCurrentPosition(
        pos => {
          commandesData.current.clientLat = pos.coords.latitude;
          commandesData.current.clientLon = pos.coords.longitude;
        },
        err => {
          alert("â›” Veuillez activer la localisation GPS pour utiliser l'application.");
          clearInterval(gpsInterval);
        }
      );
    }, 5000);
    return () => clearInterval(gpsInterval);
  }, []);

  useEffect(() => {
    socketRef.current = io('http://localhost:3000', {
      withCredentials: true,
      transports: ['websocket', 'polling'],
    });
    const socket = socketRef.current;

    socket.on('livreurStatusChange', ({ id, isOnline }) => {
      if (isOnline === 1) {
        axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
          .then(res => setLivreurs(res.data.livreurs || []));
      } else {
        setLivreurs(prev => prev.filter(l => l.id !== id));
      }
    });

    socket.on("commandeCreated", () => window.location.reload());
    socket.on("commandeConfirmed", () => window.location.reload());
    socket.on("update", () => window.location.reload());
    socket.on("commandeAnnulee", (data) => {
      alert(data.message);
      window.location.reload();
    });

    return () => socket.disconnect();
  }, []);

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const formVisible = useMemo(() => {
    return !commandes.some(cmd =>
      (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) ||
      (cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0)
    );
  }, [commandes]);

  const commandeEnCours = commandes.find(cmd => cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0);

  useEffect(() => {
    if (!commandeEnCours || !commandesData.current.clientLat) return;

    const interval = setInterval(() => {
      axios.get(`http://localhost:3000/api/comande/updatePosition/${commandeEnCours.livreur_id}`)
        .then(res => {
          const { latitude, longitude } = res.data;
          const dist = calculateDistance(latitude, longitude, commandesData.current.clientLat, commandesData.current.clientLon);
          setDistance(dist.toFixed(2));
        })
        .catch(console.error);
    }, 5000);

    return () => clearInterval(interval);
  }, [commandeEnCours]);

  // âœ…âœ…âœ… DÃ‰FINITION MANQUANTE AJOUTÃ‰E ICI
  const updateMap = () => {
    if (!commandeEnCours || !commandesData.current.clientLat || !commandesData.current.clientLon || !mapRef.current) return;

    axios.get(`http://localhost:3000/api/comande/updatePosition/${commandeEnCours.livreur_id}`)
      .then(res => {
        const { latitude, longitude } = res.data;

        if (routingRef.current) {
          routingRef.current.setWaypoints([
            L.latLng(latitude, longitude),
            L.latLng(commandesData.current.clientLat, commandesData.current.clientLon)
          ]);
        } else {
          routingRef.current = L.Routing.control({
            waypoints: [
              L.latLng(latitude, longitude),
              L.latLng(commandesData.current.clientLat, commandesData.current.clientLon)
            ],
            routeWhileDragging: false,
            show: false,
            addWaypoints: false,
            createMarker: () => null
          }).addTo(mapRef.current);
        }
      })
      .catch(console.error);
  };

  useEffect(() => {
    if (!commandeEnCours || !commandesData.current.clientLat || !commandesData.current.clientLon) return;

    const mapContainer = document.getElementById('map');
    if (!mapContainer) return;

    if (routingRef.current) {
      routingRef.current.remove();
      routingRef.current = null;
    }
    if (mapRef.current) {
      mapRef.current.remove();
      mapRef.current = null;
    }

    const map = L.map('map').setView(
      [commandesData.current.clientLat, commandesData.current.clientLon],
      13
    );
    mapRef.current = map;

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; OpenStreetMap contributors'
    }).addTo(map);

    const clientIcon = L.icon({
      iconUrl: '/images/client-icon.png',
      iconSize: [32, 32],
      iconAnchor: [16, 32]
    });

    L.marker(
      [commandesData.current.clientLat, commandesData.current.clientLon],
      { icon: clientIcon }
    ).addTo(map).bindPopup("ðŸ§â€â™‚ï¸ Client");

    const interval = setInterval(() => {
      updateMap();
    }, 5000);

    updateMap();

    return () => clearInterval(interval);
  }, [commandeEnCours]);

  const handleGeoSubmit = (livreur) => {
    navigator.geolocation.getCurrentPosition(
      async pos => {
        const payload = {
          client_id: user.id,
          client_nom: user.nom,
          client_num: user.telephone,
          livreur_id: livreur.id,
          livreur_nom: livreur.nom,
          livreur_marque_moto: livreur.marque_moto,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
          statut: "1",
          statut_2: "0",
          statut_3: "0",
          disponibilite: "0"
        };

        try {
          await axios.post('http://localhost:3000/api/commande/accueil', payload, {
            withCredentials: true
          });
          window.location.href = '/client/pages/Accueil';
        } catch (err) {
          console.error('Erreur lors de lâ€™envoi de la commande', err);
        }
      },
      err => {
        alert("â›” GPS non autorisÃ©. Impossible de continuer.");
      }
    );
  };

  if (!user) return <p>Chargement...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>

      <div className="main-content client" id="continer">
        {formVisible && (
          <>
            <h1>Bienvenue {user.nom}</h1>
            <div className="all-form" id="okjik">
              <div id="zone-livreurs">
                <h3>Les livreurs</h3>
                {livreurs.length === 0 ? (
                  <div className="alert alert-warning text-center">
                    ðŸ›‘ Aucun livreur nâ€™est actuellement en ligne.
                  </div>
                ) : (
                  livreurs.map(livreur => (
                    <div key={livreur.id} id={`livreur-${livreur.id}`} className={`livreur-card ${livreur.est_occupe ? 'occupe' : 'disponible'}`}>
                      <img src="/assets/img/service-livraison-concept-masque.png" alt="livreur" />
                      <span className="livreur-nom">{livreur.nom}</span>
                      <span>{livreur.marque_moto}</span>
                      <div className="commander">
                        {livreur.est_occupe ? (
                          <span className="statut" style={{ color: 'red' }}>ðŸ”´ OccupÃ©</span>
                        ) : (
                          <>
                            <input type="button" value="Commander" onClick={() => handleGeoSubmit(livreur)} />
                            <span className="statut" style={{ color: 'green', display: 'block' }}>ðŸŸ¢ Disponible</span>
                          </>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </>
        )}

        {commandeEnCours && (
          <div className='map-container'>
            <div id="map" style={{ height: '400px', marginTop: '20px' }}></div>
            <p>Distance restante : {distance} km</p>
            <p>Temps Ã©coulÃ© : {startTime ? Math.floor((Date.now() - startTime) / 1000) + 's' : '...'}</p>
          </div>
        )}

        {commandes.map(cmd => {
          const date = new Date(cmd.date_commande).toLocaleString();

          if (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) {
            return (
              <div key={cmd.id}>
                <table className="table table-striped mt-4">
                  <thead>
                    <tr><th>Livreur</th><th>Moto</th><th>Statut</th><th>Date</th><th>Action</th></tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{cmd.livreur_nom}</td>
                      <td>{cmd.livreur_marque_moto}</td>
                      <td>En cours</td>
                      <td>{date}</td>
                      <td>
                        <form
                          action={`/commande/annuler/${cmd.id}`}
                          method="POST"
                          onSubmit={() => confirm('Annuler ?')}
                        >
                          <button className="btn btn-danger btn-sm">Annuler</button>
                        </form>
                      </td>
                    </tr>
                  </tbody>
                </table>
                <div className="loader-container">
                  <div className="loader"></div>
                  <p>En attente de validation par le livreur...</p>
                </div>
              </div>
            );
          }

          return null;
        })}
      </div>
    </>
  );
};

export default Accueil;
