import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/attente.css';
import '../styles/accueil.css';

const Accueil = () => {
  const [user, setUser] = useState(null);
  const [commandes, setCommandes] = useState([]);
  const [livreurs, setLivreurs] = useState([]);

  // Fetch initial data + polling
  const fetchData = () => {
    axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
      .then(res => {
        const data = res.data;
        setUser(data.user);
        setCommandes(data.commandes || []);
        setLivreurs(data.livreurs || []);
      })
      .catch(console.error);
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 5000);
    const socket = io('http://localhost:3000');
    socket.on('livreurStatusChange', fetchData);
    socket.on('commandeCreated', fetchData);
    socket.on('commandeConfirmed', fetchData);
    socket.on('commandeAnnulee', fetchData);
    return () => {
      clearInterval(interval);
      socket.disconnect();
    };
  }, []);

  const handleCommande = (livreur) => {
    navigator.geolocation.getCurrentPosition(pos => {
      axios.post('http://localhost:3000/api/commande/creer', {
        client_id: user.id,
        client_nom: user.nom,
        client_num: user.telephone,
        livreur_id: livreur.id,
        livreur_nom: livreur.nom,
        livreur_marque_moto: livreur.marque_moto,
        latitude: pos.coords.latitude,
        longitude: pos.coords.longitude,
        statut: 1,
        statut_2: 0,
        statut_3: 0,
        disponibilite: 0,
      }, { withCredentials: true })
      .then(fetchData)
      .catch(console.error);
    }, () => alert("â›” Active la gÃ©oloc"));
  };

  const formVisible = useMemo(() => {
    return !commandes.some(cmd =>
      (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) ||
      (cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0)
    );
  }, [commandes]);

  if (user === null) return <p>Chargement en coursâ€¦</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user}/>
      </div>
      <div className="main-content">
        { formVisible && <>
          <h1>Bienvenue {user.nom}</h1>
          <div className="all-form" id="okjik">
            <h3>Les livreurs</h3>
            {livreurs.length === 0 ? (
              <div className="alert alert-warning">ðŸ›‘ Aucun livreur en ligne.</div>
            ) : livres.map(livreur => (
              <div key={livreur.id} className={`livreur-card ${livreur.est_occupe ? 'occupe' : 'disponible'}`}>
                <span>{livreur.nom}</span> â€“ {livreur.marque_moto}
                {livreur.est_occupe ? (
                  <span className="statut" style={{color:'red'}}>ðŸ”´ OccupÃ©</span>
                ) : (
                  <button onClick={() => handleCommande(livreur)}>Commander</button>
                )}
              </div>
            ))}
          </div>
        </> }

        { commandes.map(cmd => (
          (cmd.statut===1 && cmd.statut_2===0 && cmd.statut_3===0) && (
            <div key={cmd.id}>
              <p>Commande en cours : {cmd.livreur_nom}</p>
            </div>
          )
        )) }
      </div>
    </>
  );
};

export default Accueil;
