// === üìÅ src/pages/Dashboard.js ===
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import Header from '../Lheader/Header';
import L from 'leaflet';
import 'leaflet-routing-machine';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/acc-livreur.css';

const socket = io('http://localhost:3000');

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);
  const [position, setPosition] = useState({ lat: null, lng: null });
  const [gpsError, setGpsError] = useState(false);
  const [distanceRestante, setDistanceRestante] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);
  const mapRef = useRef(null);
  const routingRef = useRef(null);
  const intervalGps = useRef(null);
  const intervalCommande = useRef(null);
  const intervalChrono = useRef(null);

  // R√©cup√©rer user + commande √† l‚Äôarriv√©e
  useEffect(() => {
    axios.get('http://localhost:3000/api/livreur/livreurAccueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommande(res.data.commande);
      })
      .catch(console.error);
  }, []);

  // GPS tracking toutes les 5 secondes
  useEffect(() => {
    const updateGPS = () => {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          const lat = pos.coords.latitude;
          const lng = pos.coords.longitude;
          setPosition({ lat, lng });
          setGpsError(false);

          axios.post('http://localhost:3000/api/livreur/updatePosition', {
            latitude_li: lat,
            longitude_li: lng
          }, { withCredentials: true });
        },
        () => {
          setGpsError(true);
        }
      );
    };

    updateGPS();
    intervalGps.current = setInterval(updateGPS, 5000);
    return () => clearInterval(intervalGps.current);
  }, []);

  // Mise √† jour commande + affichage carte si confirm√©e
  useEffect(() => {
    if (!commande || commande.statut !== 1 || commande.statut_2 !== 1 || commande.statut_3 !== 0) return;

    const map = L.map('map').setView([commande.latitude, commande.longitude], 13);
    mapRef.current = map;

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '¬© OpenStreetMap'
    }).addTo(map);

    const iconLivreur = L.icon({
      iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
      iconSize: [32, 32]
    });

    const markerClient = L.marker([commande.latitude, commande.longitude]).addTo(map).bindPopup("üìç Client");
    const markerLivreur = L.marker([position.lat, position.lng], { icon: iconLivreur }).addTo(map).bindPopup("üöö Vous");

    routingRef.current = L.Routing.control({
      waypoints: [
        L.latLng(position.lat, position.lng),
        L.latLng(commande.latitude, commande.longitude)
      ],
      lineOptions: {
        styles: [{ color: 'blue', weight: 4 }]
      },
      createMarker: () => null,
      addWaypoints: false,
      draggableWaypoints: false,
    }).addTo(map);

    intervalCommande.current = setInterval(() => {
      axios.get('http://localhost:3000/api/livreur/livreurAccueil', { withCredentials: true })
        .then(res => {
          setCommande(res.data.commande);
        });
    }, 5000);

    intervalChrono.current = setInterval(() => {
      setElapsedTime(Math.floor((Date.now() - startTime) / 1000));
    }, 1000);

    return () => {
      clearInterval(intervalCommande.current);
      clearInterval(intervalChrono.current);
      map.remove();
    };
  }, [commande?.id, position.lat]);

  // Socket listener
  useEffect(() => {
    socket.on("commandeCreated", () => window.location.reload());
    socket.on("commandeConfirmed", () => window.location.reload());
    socket.on("commandeAnnulee", () => window.location.reload());
    return () => {
      socket.off("commandeCreated");
      socket.off("commandeConfirmed");
      socket.off("commandeAnnulee");
    };
  }, []);

  const handleAccepter = () => {
    setStartTime(Date.now());
    axios.post(`http://localhost:3000/api/commande/confirmer/${commande.id}`, {
      latitude_li: position.lat,
      longitude_li: position.lng
    }, { withCredentials: true })
      .then(() => window.location.reload());
  };

  const handleAnnuler = () => {
    if (!window.confirm("Annuler la commande ?")) return;
    axios.post(`http://localhost:3000/api/commande/annuler_li/${commande.id}`, {
      latitude_li: position.lat,
      longitude_li: position.lng
    }, { withCredentials: true })
      .then(() => window.location.reload())
      .catch(() => alert("Erreur d'annulation"));
  };

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}m ${s}s`;
  };

  if (!user) return <p>Chargement du livreur...</p>;

  return (
    <>
      <div className="mes-barre"><Header user={user} /></div>

      <div className="main-content" id="continer">
        {gpsError && (
          <div className="alert alert-danger text-center">‚õî Veuillez activer votre GPS pour continuer.</div>
        )}

        <div className="dashboard-livreur">
          <h1>Bonjour {user.nom}</h1>

          {!commande || commande.statut_3 === 1 ? (
            <div className="alert alert-secondary">Aucune demande livraison en cours</div>
          ) : commande.statut === 1 && commande.statut_2 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Accepter</th><th>Refuser</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td><button className="btn btn-success btn-sm" onClick={handleAccepter}>Accepter</button></td>
                    <td><button className="btn btn-danger btn-sm" onClick={handleAnnuler}>Annuler</button></td>
                  </tr>
                </tbody>
              </table>
              <div className="loader-container">
                <div className="loader"></div>
                <p>En attente de validation...</p>
              </div>
            </>
          ) : commande.statut === 1 && commande.statut_2 === 1 ? (
            <>
              <table className="table table-striped">
                <thead><tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Refuser</th></tr></thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td><button className="btn btn-danger btn-sm" onClick={handleAnnuler}>Annuler</button></td>
                  </tr>
                </tbody>
              </table>

              <div className="map-container mt-4">
                <div id="map" style={{ height: '400px' }}></div>
                <p className="mt-3">
                  <strong>Distance restante :</strong> Calcul en cours... <br />
                  <strong>Temps mis :</strong> {formatTime(elapsedTime)}
                </p>
              </div>
            </>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
