import React, { useEffect, useState, useMemo, useRef } from 'react';
import axios from 'axios';
import Header from '../header/Header';
import L from 'leaflet';
import { io } from 'socket.io-client';

import '../styles/barre.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3000/api/livreur/accueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommande(res.data.commande);
      })
      .catch(err => {
        console.error(err);
        // Optionnel: redirection si non connecté
      });
  }, []);

  useEffect(() => {
    if (!commande || !commande.latitude || !commande.longitude) return;

    const map = L.map('map').setView([commande.latitude, commande.longitude], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap'
    }).addTo(map);

    L.marker([commande.latitude, commande.longitude]).addTo(map).bindPopup("📍 Client");
    L.marker([commande.latitude_li, commande.longitude_li], {
      icon: L.icon({
        iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
        iconSize: [32, 32]
      })
    }).addTo(map).bindPopup("🚚 Vous");

    const line = L.polyline([[commande.latitude, commande.longitude], [commande.latitude_li, commande.longitude_li]], {
      color: 'blue'
    }).addTo(map);

    map.fitBounds(line.getBounds());

    return () => map.remove();
  }, [commande]);

  if (!user) return <p>Chargement du livreur...</p>;

  return (
    <>
        <div className="mes-barre">
            <Hader user={user} />
        </div>
        <div className="main-content" id="continer">
            <div className="dashboard-livreur">
            <h1>Bonjour {user.nom}</h1>

            {!commande || (commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 1) ? (
                <div className="alert alert-info">Commande annulée ou terminée. En attente d'une autre.</div>
            ) : commande.statut === 1 && commande.statut_2 === 0 && commande.statut_3 === 0 ? (
                <>
                <table className="table table-striped">
                    <thead>
                    <tr><th>Client</th><th>Numéro</th><th>Date</th><th>Accepter</th></tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>{commande.client_nom}</td>
                        <td>{commande.client_num}</td>
                        <td>{new Date(commande.date_commande).toLocaleString()}</td>
                        <td>
                        <form method="POST" action={`http://localhost:3000/commande/confirmer/${commande.id}`}>
                            <input type="hidden" name="latitude_li" value={commande.latitude_li} />
                            <input type="hidden" name="longitude_li" value={commande.longitude_li} />
                            <button type="submit" className="btn btn-success btn-sm">Accepter</button>
                        </form>
                        </td>
                    </tr>
                    </tbody>
                </table>
                <div className="loader-container">
                    <div className="loader"></div>
                    <p>En attente de validation...</p>
                </div>
                </>
            ) : commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0 ? (
                <>
                <table className="table table-striped">
                    <thead>
                    <tr><th>Client</th><th>Numéro</th><th>Date</th><th>Refuser</th></tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>{commande.client_nom}</td>
                        <td>{commande.client_num}</td>
                        <td>{new Date(commande.date_commande).toLocaleString()}</td>
                        <td>
                        <form method="POST" action={`http://localhost:3000/commande/annuler_li/${commande.id}`}>
                            <input type="hidden" name="latitude_li" value={commande.latitude_li} />
                            <input type="hidden" name="longitude_li" value={commande.longitude_li} />
                            <button type="submit" className="btn btn-danger btn-sm">Annuler</button>
                        </form>
                        </td>
                    </tr>
                    </tbody>
                </table>

                <div className="map-container mt-4">
                    <div id="map" style={{ height: '400px' }}></div>
                </div>
                </>
            ) : null}
            </div>
        </div>
    </>
  );
};


export default Dashboard;
