import React, { useEffect, useState, useMemo, useRef } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import L from 'leaflet';
import { io } from 'socket.io-client';

import '../styles/barre.css';
import '../styles/attente.css';
import '../styles/accueil.css';

const Accueil = () => {
  const [user, setUser] = useState(null);
  const [commandes, setCommandes] = useState([]);
  const [livreurs, setLivreurs] = useState([]);
  const commandesData = useRef({});
  const mapRef = useRef(null);
  const clientMarker = useRef(null);
  const livreurMarker = useRef(null);
  const polyline = useRef(null);

  const chargerInfos = () => {
    axios.get('http://localhost:3000/api/commande/accueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommandes(res.data.commandes);
        setLivreurs(res.data.livreurs);
      })
      .catch(console.error);
  };

  useEffect(chargerInfos, []);

  useEffect(() => {
    const socket = io('http://localhost:3000');
    console.log("🟢 Connecté à Socket.io");

    socket.on('livreurStatusChange', ({ id, status }) => {
      const card = document.querySelector(`#livreur-${id}`);
      if (!card) return;

      const statutEl = card.querySelector('.statut');

      if (status === 'online') {
        card.classList.remove('occupe');
        card.classList.add('disponible');
        if (statutEl) {
          statutEl.textContent = '🟢 Disponible';
          statutEl.style.color = 'green';
        }
        const btn = card.querySelector('input[type="submit"]');
        if (btn) btn.disabled = false;
      } else if (status === 'offline') {
        card.remove();
        const cards = document.querySelectorAll('.livreur-card.disponible');
        const zone = document.querySelector('#zone-livreurs');
        if (cards.length === 0 && zone) {
          zone.innerHTML = `
            <div class="alert alert-warning text-center">
              🛑 Tous les livreurs sont indisponibles. Veuillez patienter...
            </div>
          `;
        }
      }
    });

    socket.on("commandeCreated", chargerInfos);
    socket.on("commandeConfirmed", chargerInfos);
    socket.on("update", chargerInfos);
    socket.on("commandeAnnulee", data => {
      alert(data.message);
      chargerInfos();
    });

    return () => socket.disconnect();
  }, []);

  const formVisible = useMemo(() => {
    return !commandes.some(cmd =>
      (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) ||
      (cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0)
    );
  }, [commandes]);

  const handleGeoSubmit = (livreur) => {
    navigator.geolocation.getCurrentPosition(
      pos => {
        commandesData.current = {
          client_id: user.id,
          client_nom: user.nom,
          client_num: user.telephone,
          livreur_id: livreur.id,
          livreur_nom: livreur.nom,
          livreur_marque_moto: livreur.marque_moto,
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude,
          statut: "1",
          statut_2: "0",
          statut_3: "0",
          disponibilite: "0"
        };

        const tempForm = document.createElement("form");
        tempForm.method = "POST";
        tempForm.action = "/commande/html/page/accueil";

        for (let key in commandesData.current) {
          const input = document.createElement("input");
          input.type = "hidden";
          input.name = key;
          input.value = commandesData.current[key];
          tempForm.appendChild(input);
        }

        document.body.appendChild(tempForm);
        tempForm.submit();
      },
      err => {
        alert("⛔ GPS non autorisé. Impossible de continuer.");
      }
    );
  };

  // Affichage de la carte s’il y a une commande en cours
  useEffect(() => {
    const commandeActive = commandes.find(
      cmd => cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0
    );
    if (!commandeActive || !commandeActive.latitude || !commandeActive.longitude) return;

    if (!mapRef.current) {
      mapRef.current = L.map('map').setView([commandeActive.latitude, commandeActive.longitude], 13);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap'
      }).addTo(mapRef.current);

      clientMarker.current = L.marker([commandeActive.latitude, commandeActive.longitude]).addTo(mapRef.current).bindPopup("📍 Client");

      livreurMarker.current = L.marker([commandeActive.latitude_li, commandeActive.longitude_li], {
        icon: L.icon({
          iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
          iconSize: [32, 32]
        })
      }).addTo(mapRef.current).bindPopup("🚚 Vous");

      polyline.current = L.polyline([[commandeActive.latitude, commandeActive.longitude], [commandeActive.latitude_li, commandeActive.longitude_li]], {
        color: 'blue'
      }).addTo(mapRef.current);

      mapRef.current.fitBounds(polyline.current.getBounds());
    } else {
      clientMarker.current.setLatLng([commandeActive.latitude, commandeActive.longitude]);
      livreurMarker.current.setLatLng([commandeActive.latitude_li, commandeActive.longitude_li]);
      polyline.current.setLatLngs([[commandeActive.latitude, commandeActive.longitude], [commandeActive.latitude_li, commandeActive.longitude_li]]);
      mapRef.current.fitBounds(polyline.current.getBounds());
    }
  }, [commandes]);

  if (!user) return <p>Chargement...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>

      <div className="main-content" id="continer">
        {formVisible && (
          <>
            <h1>Bienvenue {user.nom}</h1>
            <div className="all-form" id="okjik">
              <div id="zone-livreurs">
                <h3>Le livreurs</h3>
                {livreurs.length === 0 ? (
                  <div className="alert alert-warning text-center">
                    🛑 Aucun livreur n’est actuellement en ligne.
                  </div>
                ) : (
                  livreurs.map(livreur => (
                    <div
                      key={livreur.id}
                      id={`livreur-${livreur.id}`}
                      className={`livreur-card ${livreur.est_occupe ? 'occupe' : 'disponible'}`}
                    >
                      <img src="/assets/img/service-livraison-concept-masque.png" alt="livreur" />
                      <span className="livreur-nom">{livreur.nom}</span>
                      <span>{livreur.marque_moto}</span>
                      <div className="commander">
                        {livreur.est_occupe ? (
                          <span className="statut" style={{ color: 'red' }}>
                            🔴 Occupé
                          </span>
                        ) : (
                          <>
                            <input
                              type="button"
                              value="Commander"
                              onClick={() => handleGeoSubmit(livreur)}
                            />
                            <span className="statut" style={{ color: 'green', display: 'block' }}>
                              🟢 Disponible
                            </span>
                          </>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </>
        )}

        {commandes.map(cmd => {
          const date = new Date(cmd.date_commande).toLocaleString();

          if (cmd.statut === 1 && cmd.statut_2 === 0 && cmd.statut_3 === 0) {
            return (
              <div key={cmd.id}>
                <table className="table table-striped mt-4">
                  <thead>
                    <tr><th>Livreur</th><th>Moto</th><th>Statut</th><th>Date</th><th>Action</th></tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{cmd.livreur_nom}</td>
                      <td>{cmd.livreur_marque_moto}</td>
                      <td>En cours</td>
                      <td>{date}</td>
                      <td>
                        <form
                          action={`/commande/annuler/${cmd.id}`}
                          method="POST"
                          onSubmit={() => confirm('Annuler ?')}
                        >
                          <button className="btn btn-danger btn-sm">Annuler</button>
                        </form>
                      </td>
                    </tr>
                  </tbody>
                </table>
                <div className="loader-container">
                  <div className="loader"></div>
                  <p>En attente de validation par le livreur...</p>
                </div>
              </div>
            );
          }

          if (cmd.statut === 1 && cmd.statut_2 === 1 && cmd.statut_3 === 0) {
            return (
              <div key={cmd.id} className="map-container mt-4">
                <h4>Commande en cours - {new Date(cmd.date_commande).toLocaleTimeString()}</h4>
                <div id="map" style={{ height: '400px' }}></div>
              </div>
            );
          }

          return null;
        })}
      </div>
    </>
  );
};

export default Accueil;
