import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import Header from '../Lheader/Header';
import L from 'leaflet';
import '../styles/barre.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [commande, setCommande] = useState(null);
  const mapRef = useRef(null);
  const intervalRef = useRef(null);

  const fetchData = () => {
    axios.get('http://localhost:3000/api/livreur/livreurAccueil', { withCredentials: true })
      .then(res => {
        setUser(res.data.user);
        setCommande(res.data.commande);
      })
      .catch(err => {
        console.error('Erreur chargement donn√©es :', err);
      });
  };

  useEffect(() => {
    fetchData();
    intervalRef.current = setInterval(fetchData, 5000);
    return () => clearInterval(intervalRef.current);
  }, []);

  useEffect(() => {
    if (
      !commande ||
      !commande.latitude ||
      !commande.longitude ||
      !(commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0)
    ) return;

    const mapContainer = document.getElementById('map');
    if (!mapContainer) return;

    if (mapRef.current) {
      mapRef.current.remove();
      mapRef.current = null;
    }

    const map = L.map('map').setView([commande.latitude, commande.longitude], 13);
    mapRef.current = map;

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '¬© OpenStreetMap'
    }).addTo(map);

    L.marker([commande.latitude, commande.longitude]).addTo(map).bindPopup("üìç Client");

    L.marker([commande.latitude_li, commande.longitude_li], {
      icon: L.icon({
        iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
        iconSize: [32, 32]
      })
    }).addTo(map).bindPopup("üöö Vous");

    const line = L.polyline([[commande.latitude, commande.longitude], [commande.latitude_li, commande.longitude_li]], {
      color: 'blue'
    }).addTo(map);

    map.fitBounds(line.getBounds());

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, [commande]);

  if (!user) return <p>Chargement du livreur...</p>;

  return (
    <>
      <div className="mes-barre">
        <Header user={user} />
      </div>
      <div className="main-content" id="continer">
        <div className="dashboard-livreur">
          <h1>Bonjour {user.nom}</h1>

          {!commande || (commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 1) ? (
            <div className="alert alert-info">Commande annul√©e ou termin√©e. En attente d'une autre.</div>
          ) : commande.statut === 1 && commande.statut_2 === 0 && commande.statut_3 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Accepter</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td>
                      <button
                        type="button"
                        className="btn btn-success btn-sm"
                        onClick={() => {
                          axios.post(`http://localhost:3000/commande/confirmer/${commande.id}`, {
                            latitude_li: commande.latitude_li,
                            longitude_li: commande.longitude_li
                          }, { withCredentials: true })
                            .then(() => fetchData())
                            .catch(console.error);
                        }}
                      >
                        Accepter
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
              <div className="loader-container">
                <div className="loader"></div>
                <p>En attente de validation...</p>
              </div>
            </>
          ) : commande.statut === 1 && commande.statut_2 === 1 && commande.statut_3 === 0 ? (
            <>
              <table className="table table-striped">
                <thead>
                  <tr><th>Client</th><th>Num√©ro</th><th>Date</th><th>Refuser</th></tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{commande.client_nom}</td>
                    <td>{commande.client_num}</td>
                    <td>{new Date(commande.date_commande).toLocaleString()}</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => {
                          axios.post(`http://localhost:3000/commande/annuler_li/${commande.id}`, {
                            latitude_li: commande.latitude_li,
                            longitude_li: commande.longitude_li
                          }, { withCredentials: true })
                            .then(() => fetchData())
                            .catch(console.error);
                        }}
                      >
                        Annuler
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>

              <div className="map-container mt-4">
                <div id="map" style={{ height: '400px' }}></div>
              </div>
            </>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
