import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';

import Connect from './pages/Connect';
import Inscrip from './pages/Inscrip';
import AdminApp from './admin/AdminApp';
import ClientApp from './client/ClientApp';
import LivreurApp from './livreur/LivreurApp';
import Accueil from './client/pages/Accueil'; // ✅ Import du composant

const RoutesConfig = () => {
  return (
    <Routes>
      {/* Redirection racine */}
      <Route path="/" element={<Navigate to="/pages/Accueil" />} />

      {/* Pages publiques */}
      <Route path="/connect" element={<Connect />} />
      <Route path="/inscrip" element={<Inscrip />} />

      {/* Page d'accueil client après login */}
      <Route path="/client/pages/Accueil" element={<Accueil />} /> {/* ✅ Cette ligne */}

      <Route path="/pages/Accueil" element={<Accueil />} /> {/* ✅ Cette ligne */}

      {/* Espaces Admin / Client / Livreur */}
      <Route path="/admin/*" element={<AdminApp />} />
      <Route path="/client/*" element={<ClientApp />} />
      <Route path="/livreur/*" element={<LivreurApp />} />
    </Routes>
  );
};

export default RoutesConfig;
